import { useEffect, useRef } from 'react';
import axios from 'axios';

const SreachProduct = ({ name }) => {
    const getData = useRef({});
    useEffect(() => {
        console.log(name)
        const fetchData = async () => {
            try {
                const res = await axios.get(`http://localhost:8000/api/products?page=2&limit=5&name=${name}`);
                console.log(res.data);
                return res;
            } catch (error) {
                console.error(error);
            }
        };

        fetchData().then(data => {
            getData.current = data;
        });
    }, [name]);

    // return null;
};

export default SreachProduct;
