import { configureStore } from '@reduxjs/toolkit';
import productReducer from './Slice/cartSlice';
import {
  persistStore,
  persistReducer,
  FLUSH,
  REHYDRATE,
  PAUSE,
  PERSIST,
  PURGE,
  REGISTER,
} from 'redux-persist'

const store = configureStore({
  reducer: {
    cart: productReducer,
  },
});

export default store;
// export const persistor = persistStore(store);
