import { useEffect, useState } from 'react';
import axios from 'axios';

const SreachProduct = ({ name }) => {
    const [data, setData] = useState(null);

    useEffect(() => {
        console.log(name)
        const fetchData = async () => {
            try {
                const res = await axios.get(`http://localhost:8000/api/products?limit=5&name=${name}`);
                setData(res)
            } catch (error) {
                console.log(error);
            }
        };
        fetchData();
    }, [name]);

    return null;
};

export default SreachProduct;
