import React, { useState } from 'react';

const InputFrom = ({nameLabel, type , idInput, name, placeholder }) => {
    const [Customers, setCustomers] = useState({
        name: "",
        address: "",
        phone: "",
        email: "",
    })
    const handleInputChange = (e) => {
        setCustomers({
            ...Customers,
            [e.target.name]: e.target.value,
        })
    }

    return (
        <div className='flex items-center mb-5' >
            <label htmlFor="customerEmail" className='w-[120px] mr-5 text-lg font-medium text-black'>{`${nameLabel} :`} </label>
            <input
                autoComplete='off'
                type={type}
                placeholder='Phone customer'
                id="customerEmail"
                onChange={handleInputChange}
                name='email'
                className='w-full px-2 py-3 text-base font-normal text-black border border-[#DEDEDE] rounded-md focus:outline-none' />
        </div>
    );
};

export default InputFrom;
