import { useEffect, useRef } from 'react';
import axios from 'axios';

const SreachProduct = ({ name }) => {
    const getData = useRef({});

    useEffect(() => {
        const fetchData = async () => {
            try {
                const res = await axios.get(`https:http://localhost:8000/api/products?page=2&limit=4&name=${name}`);
                console.log(res.data);
                return res;
            } catch (error) {
                console.error(error);
            }
        };

        getData.current = fetchData();
    }, [name]);

    return null;
};

export default SreachProduct;
