import { useEffect } from 'react';
import { useLocation } from 'react-router-dom';

function ScrollToTopOnPathChange() {
  const location = useLocation();
  useEffect(() => {
    window.scrollTo(0, 0);
  }, [location.pathname]);

  return null;
}

export default ScrollToTopOnPathChange;
