import React, { Fragment } from 'react';
import { useSelector } from 'react-redux';
import { selectCart } from '../../redux/Slice/cartSlice';


const GetProductInCart = () => {
    const productCart = useSelector(selectCart);
    console.log(productCart.cartItems[0].thumbnailURL)
    return (
        <Fragment>
            {productCart.cartItems.map((item, index) => (
                <div className='flex items-center justify-start product-in-cart '>
                    <div className='w-4/12 h-full '>
                        <img src={`http://localhost:8000/api/products/image/${item.thumbnailURL}`} alt="" className='h-[300px] w-[320px] cursor-pointer object-cover' />
                    </div>
                    <div className='w-8/12 h-full'>
                        <img src={`http://localhost:8000/api/products/image/${item.thumbnailURL}`} alt="" className='h-[300px] w-[320px] cursor-pointer object-cover' />
                    </div>
                </div>
            ))}
        </Fragment>

    );
};

export default GetProductInCart;
