import { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const SreachProduct = ({ name }) => {
    const [data, setData] = useState([]);

    useEffect(() => {
        // console.log(data.data)
        const fetchData = async () => {
            try {
                const res = await axios.get(`http://localhost:8000/api/products?limit=5&name=${name}`);
                setData(res.data)
            } catch (error) {
                console.log(error);
            }
        };
        fetchData();
    }, [name]);

    return <div className='absolute top-16 w-[400px] px-3 bg-gray-100 border border-black rounded-b-md'>
        {data && data.data && data.data.map((item, index) => (
            <Link to={`/products-details/${item._id}`} key={item._id} className='flex items-center justify-between my-2'>
                <p className='text-base font-medium text-black cursor-pointer'>{item.name}</p>
                <p className='font-medium text-red-600'>{item.price}</p>
            </Link>
        ))}
    </div>;
};

export default SreachProduct;
