import axios from 'axios';
import { useEffect, useRef } from 'react'

export default function SreachProduct({ name }) {

    const getData = useRef({})
    getData.current = async () => {
        try {
            // setLoading(false)
            // setHits(res.data?.hits || []);
            const res = await axios.get(`https://hn.algolia.com/api/v1/search?page=2&limit=4&name=${name}`);
            console.log(res.data)
            return res
        } catch (error) {
            // setLoading(true)
        }
    }
    useEffect(() => {
        getData.current();
    }, [name])

}
