import React, { useEffect, useRef } from 'react';
import FormContainer from '../common/FormContailer'
import GetProductById from '../API/GetProductById';
import Header from '../HomePage/Header';
import Breadcrumb from '../common/Breadcrumb';
import GetProduct from '../API/GetProducts';

const ProductDetailsPageMain = () => {
    const topRef = useRef(null);
    useEffect(() => {
        window.scrollTo(0, 0);
    }, []);
    useEffect(() => {
        console.log(topRef.current)
        if (topRef.current) {
            topRef.current.scrollIntoView({ behavior: 'smooth' });
        }
    }, [topRef]);
    return (
        <div>
            <Header></Header>
            <FormContainer>
                <Breadcrumb className='py-6'></Breadcrumb>
                <GetProductById topRef={topRef}></GetProductById>
                <div className='mt-24'>
                    <h3 className='pb-8 text-3xl font-medium'>You Might Also Like</h3>
                    <GetProduct className='grid-cols-4 gap-x-5 gap-y-10' page={1} limit={4}></GetProduct>
                </div>
            </FormContainer>
        </div>
    );
};

export default ProductDetailsPageMain;
