import React, { useEffect, useRef, useState } from 'react';

const ContentProductPage = () => {
    const [show, setShow] = useState(false);
    const RefDrop = useRef(null);

    useEffect(() => {
        const handleClick = (e) => {
            if (RefDrop.current && !RefDrop.current.contains(e.target)) {
                setShow(false);
            }
        };

        document.addEventListener("click", handleClick);

        return () => {
            document.removeEventListener("click", handleClick);
        };
    }, []); // Đảm bảo truyền một mảng phụ thuộc trống để hook chỉ chạy một lần khi mount

    return (
        <div className='max-w-[1280px] mb-0 mt-0 ml-auto mr-auto p-10'>
            <div className='flex justify-between'>
                <div className='action-page'>
                    {/* Nội dung của bạn */}
                </div>
                <div className=''>
                    <div
                        className="p-5 border border-gray-400  w-full cursor-pointer flex justify-center items-center"
                        onClick={() => {
                            setShow(!show);
                        }}>
                        {/* Nội dung của bạn */}
                    </div>
                    {show && (
                        <div ref={RefDrop} className=" border border-gray-400  w-full cursor-pointer">
                            <div className="p-5 cursor-pointer">Tăng dần</div>
                            <div className="p-5 cursor-pointer">Giảm dần</div>
                            <div className="p-5 cursor-pointer">Hot</div>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default ContentProductPage;
