import React, { useEffect, useRef, useState } from 'react';
import FormContainer from '../common/FormContailer';
import GetProduct from '../API/GetProducts';

const SidebarOption = ({ title, showState, setShowState, options }) => (
  <div className='mb-10'>
    <div className="cursor-pointer flex justify-between items-center w-[230px]"
      onClick={() => setShowState(!showState)}>
      <h3 className='font-normal text-base mr-4 w-[100px]'>{title}</h3>
      <svg width="15" height="8" viewBox="0 0 15 8" fill="none" xmlns="http://www.w3.org/2000/svg">
        <path d="M1.57575 0.270363C1.21527 -0.090121 0.63081 -0.090121 0.270326 0.270363C-0.0901585 0.630847 -0.0901585 1.21531 0.270326 1.57579L6.42417 7.72964C6.78466 8.09012 7.36912 8.09012 7.7296 7.72964L13.8834 1.57579C14.2439 1.21531 14.2439 0.630847 13.8834 0.270363C13.523 -0.090121 12.9385 -0.090121 12.578 0.270363L7.07689 5.7715L1.57575 0.270363Z" fill="black" />
      </svg>
    </div>
    {showState &&
      (<div className="cursor-pointer">
        {options.map((option, index) => (
          <div key={index} className="flex items-center mt-2 text-black cursor-pointer">
            <input className='w-5 h-5 checked:bg-black focus:bg-black' type="checkbox" id={option} name={option} value={option} />
            <label className='pl-3' htmlFor={option}>{option}</label>
          </div>
        ))}
      </div>)
    }
  </div>
);

const ContentProductPage = () => {
  const [show, setShow] = useState(false);
  const [arrange, setArrange] = useState("Ascending");
  const RefDrop = useRef(null);

  const ChangeArrange = (value) => {
    setArrange(value);
    setShow(false);
  };

  useEffect(() => {
    const handleClick = (e) => {
      if (RefDrop.current && !RefDrop.current.contains(e.target)) {
        setShow(false);
      }
    };

    document.addEventListener("click", handleClick);

    return () => {
      document.removeEventListener("click", handleClick);
    };
  }, []);

  const sidebarOptions = [
    { title: 'Gender', state: 'showSidebarGender', setState: 'setshowSidebarGender', options: ['Male', 'Female'] },
    { title: 'Price Range', state: 'showSidebarPrice', setState: 'setshowSidebarPrice', options: ['Below $50', 'Above $50'] },
    { title: 'Size', state: 'showSidebarSize', setState: 'setshowSidebarSize', options: ['XS (UK 4-6)', 'S (UK 8-10)', 'M (UK 12-14)', 'L (UK 12-14)'] },
    { title: 'Football Team', state: 'showSidebarFootball', setState: 'setshowSidebarFootball', options: ['FC Barcelona', 'AS Roma', 'Athletico Madrid', 'Paris Saint-Germain'] },
    { title: 'Jersey Type', state: 'showSidebarType', setState: 'setshowSidebarType', options: ['Home', 'Away', 'Custom'] },
  ];

  return (
    <FormContainer className='pt-10'>
      {/* ...other content */}
      <div className='grid grid-cols-3 gap-4 mt-10'>
        <div className='col-span-1 sidebar'>
          {sidebarOptions.map((option, index) => (
            <SidebarOption
              key={index}
              title={option.title}
              showState={option.state}
              setShowState={option.setState}
              options={option.options}
            />
          ))}
        </div>
        <div className='col-span-2'>
          <GetProduct className='grid-cols-3 gap-x-5 gap-y-10'></GetProduct>
        </div>
      </div>
    </FormContainer>
  );
};

export default ContentProductPage;
