import { useEffect } from 'react';
import { unstable_HistoryRouter } from 'react-router-dom';

const ScrollToTop = () => {
    const history = unstable_HistoryRouter();

    useEffect(() => {
      const unlisten = history.listen(() => {
        window.scrollTo(0, 0);
      });
      return () => {
        unlisten();
      };
    }, [history]);
};

export default ScrollToTop;
