import { Fragment, useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const GetProductById = ({ children, className = "" }) => {

    const [data, setData] = useState(null);

    const { id } = useParams();
   

    useEffect(() => {
        console.log("id>>>> " , id)
        const fetchData = async () => {
            try {
                const response = await axios.get(`http://localhost:8000/api/products/id/${id}`);
                setData(response.data);
            } catch (error) {
                console.error('Error fetching data: ', error);
            }
        };
        
        fetchData();
    }, []);
    
    console.log(data)
    return (
        <Fragment>
        {data ? (
            
            <div className={`grid product ${className}`}>
                    <div className='flex flex-col flex-1 item_product '>
                            <img src={`http://localhost:8000/api/products/image/${data.thumbnailURL}`} alt={data.name} className='h-[270px] w-full object-cover rounded cursor-pointer' />
                        <div className='flex flex-col flex-1 p-1 pt-5 info-product'>
                                <h3 className='mb-3 text-base text-black cursor-pointer'>{data.name}</h3>
                            <span className='mt-auto text-red-600 '>{`${data.price} $`}</span>
                        </div>
                    </div>
               
            </div>
        ) : (<h1>Loading....</h1>)}
        <div>data</div>
    </Fragment>
    );
};

export default GetProductById;
