import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ImageComponent = () => {
  const [imagePaths, setImagePaths] = useState([]);

  useEffect(() => {
    // Gọi API để lấy danh sách đường dẫn của các ảnh từ máy chủ Node.js
    axios.get('http://localhost:8000/api/products') // Thay 'URL_API/images' bằng URL thực của API của bạn
      .then(response => {
        // Lấy danh sách đường dẫn của các ảnh từ phản hồi
        const paths = response.data;
        setImagePaths(paths);
      })
      .catch(error => {
        console.error('Lỗi khi lấy danh sách ảnh:', error);
      });
  }, []);

  return (
    <div>
      {imagePaths.map((imagePath, index) => (
        <img key={index} src={imagePath} alt={`Ảnh ${index}`} />
      ))}
    </div>
  );
};

export default ImageComponent;
