import { Fragment, useEffect, useRef, useState } from 'react';
import axios from 'axios';
import { Link, useParams } from 'react-router-dom';
import Dropdown from "../common/Dropdown"

const GetProductById = ({ children, className = "",topRef }) => {
    const [data, setData] = useState(null);
    const { id } = useParams();
    const localTopRef = useRef(null);

    useEffect(() => {
        // Update the localTopRef when topRef changes
        localTopRef.current = topRef?.current;
    }, [topRef]);

    const SizeArr = [
        { id: 1, name: "36" },
        { id: 2, name: "37" },
        { id: 3, name: "39" }
    ];

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get(`http://localhost:8000/api/products/id/${id}`);
                setData(response.data);
                if (localTopRef.current) {
                    localTopRef.current.scrollIntoView({ behavior: 'smooth' });
                }
            } catch (error) {
                console.error('Lỗi khi lấy dữ liệu: ', error.response);
            }
        };
        fetchData();
    }, [id]);

    return (
        <Fragment>
            {data && data.data ? (
                <div className={`flex justify-center product ${className}`}>
                    <div className='block w-1/12 mx-2 item_product'>
                        <div>
                            <img src={`http://localhost:8000/api/products/image/${data.data.imgDescription}`} alt="" />
                        </div>
                    </div>
                    <div className='flex flex-col flex-1 w-7/12 mx-3'>
                        <img src={`http://localhost:8000/api/products/image/${data.data.thumbnailURL}`} alt={data.data.name} className='h-[500px] w-auto  object-cover rounded' />
                    </div>
                    <div className='w-4/12 mx-2'>
                        <div className='info'>
                            <h3 className='mb-3 text-2xl font-normal text-black cursor-pointer'>{data.data.name}</h3>
                            <p className='my-5 price text-[#B02B2B] font-medium text-3xl'> {data.data.price}</p>
                        </div>
                        <div className='my-5 selec-size'>
                            <div className='flex justify-between '>
                                <span className='text-[#848484] text-lg font-medium'>Size</span>
                                <Link to="#">
                                    <span className='text-[#868686] underline text-lg font-medium'>Size guide</span>
                                </Link>
                            </div>
                            <Dropdown className="w-full border border-[#DEDEDE] p-4" data={SizeArr} table={"Please Select"} status={false}></Dropdown>
                        </div>
                    </div>
                </div>
            ) : (
                <h1>Loading....</h1>
            )}
        </Fragment>
    );
};

export default GetProductById;
