import axios from 'axios';
import React, { useEffect, useState } from 'react';

const SearchApi = ({ keySeaRch }) => {
    const [data, setData] = useState({});
    console.log(data);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get(`https://nntshop.onrender.com/api/products?query=${keySeaRch}&limit=${5}`);
                setData(response.data);
            } catch (error) {
                console.error('Error: ', error.response);
            }
        };
        fetchData();
    }, [keySeaRch]);

    return (
        <div>
            {data ? <></> : data.data.map((item, index) => (
                <div key={index}>
                    <div className='flex justify-start my-8 product-in-cart'>
                        <p className='text-xl font-normal text-black sm:text-base'>{item.name}</p>
                        <p className='mt-3 text-[#B02B2B] font-medium text-2xl sm:text-lg sm:mt-2'>{`$ ${item.price}`}</p>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default SearchApi;
