import axios from 'axios';
import React, { useEffect, useState } from 'react';

const SearchApi = ({ keySeaRch }) => {
    const [data, setData] = useState({});
    console.log(data)
    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get(`https://nntshop.onrender.com/api/products?query=${keySeaRch}&limit=${5}`);
                setData(response.data);

            } catch (error) {
                console.error('Error: ', error.response);
            }
        };
        fetchData();
    }, [keySeaRch]);

    return (
        <div>
            {data.length > 0 && data.data.forEach((item, index) => (
                <div key={item.index}>
                    <h3>{item.name}</h3>
                    <span className='mt-auto font-medium text-red-600 '>{`${item.price} $`}</span>
                </div>

            ))}
        </div>
    );
};

export default SearchApi;
