export default function CreateOrder(dataCustomers) {
    let checkData = false;
    for (const field in dataCustomers) {
        if (dataCustomers[field] === "") {
            checkData = false
        } else {
            checkData = true
        }
    }
    if (checkData) {
        let Order = {
            "phone": "123456229",
            "address": "haNoi",
            "nameCustome": "Nguyễn Văn A",
            "email": "adad@gmail.com",
            "product_orders": [
                {
                    "product_id": "65869db2949896aa31d71f12",
                    "quantity": 2,
                    "size": "L"
                },
                {
                    "product_id": "65869df4949896aa31d71f14",
                    "quantity": 1,
                    "size": "M"
                }
            ],
            "status": "pending"
        }

    }
}
